// Validate input
const required = ['csvUrl'];
const errors = [];

required.forEach(field => {
  if (!$json[field]) errors.push(`Missing: ${field}`);
});

if ($json.csvUrl && !$json.csvUrl.match(/^https?:\/\/.*\.(csv|txt)$/i)) {
  errors.push('Invalid CSV URL');
}

if (errors.length) throw new Error(errors.join(', '));

const product = $json.product || {};
return [{
  json: {
    csvUrl: $json.csvUrl,
    product: {
      brand: product.brand || $json.brand || 'Unknown',
      model: product.model || $json.model || 'Unknown',
      year: product.year || $json.year || 'Unknown',
      condition: product.condition || $json.condition || 'Used',
      serial: product.serial || $json.serial || 'N/A'
    },
    callbackBaseUrl: $json.callbackBaseUrl || 'https://YOUR_N8N_URL',
    config: {
      maxOffers: Math.min($json.config?.maxOffers || 100, 500),
      timeoutMinutes: Math.min($json.config?.timeoutMinutes || 60, 240),
      batchSize: Math.min($json.config?.batchSize || 25, 100)
    },
    requestId: 'req_' + Math.random().toString(36).substr(2, 9),
    timestamp: new Date().toISOString()
  }
}];

// Validate resellers
const validated = items.map((item, index) => {
  const r = item.json;
  if (!r.id && !r.reseller_id) return null;
  if (!r.name) return null;
  if (!r.email && !r.api_url) return null;

  return {
    json: {
      id: r.id || r.reseller_id || r.email,
      name: r.name,
      email: r.email,
      api_url: r.api_url,
      region: r.region || 'Unknown',
      trust_score: parseFloat(r.trust_score || 5.0),
      specialty: r.specialty || 'General'
    }
  };
}).filter(Boolean);

if (validated.length === 0) {
  throw new Error('No valid resellers found');
}

return validated;

// Compose outreach messages
const originalInput = $items(0,0).json;
const product = originalInput.product;
const callbackUrl = originalInput.callbackBaseUrl.replace(/\/$/,'') + '/webhook/disposable-marketplace/offer';
const requestId = originalInput.requestId;

return items.map(item => {
  const r = item.json;

  const apiBody = {
    request_id: requestId,
    reseller_id: r.id,
    product,
    request_type: 'quote',
    reply_url: callbackUrl,
    deadline: new Date(Date.now() + (originalInput.config.timeoutMinutes * 60 * 1000)).toISOString()
  };

  const subject = `Quote Request: ${product.brand} ${product.model} (${product.year})`;
  const emailText = `Hello ${r.name},\n\nWe have a ${product.brand} ${product.model} (${product.year}) available:\n\nâ€¢ Condition: ${product.condition}\nâ€¢ Serial: ${product.serial}\n\nPlease provide your best cash offer within ${originalInput.config.timeoutMinutes} minutes.\n\nRespond to: ${callbackUrl}\nRequest ID: ${requestId}\n\nRequired JSON format:\n{\n  \"reseller_id\": \"${r.id}\",\n  \"price\": 0000,\n  \"currency\": \"USD\",\n  \"terms\": \"Your terms\"\n}\n\nThank you.`;

  return {
    json: {
      ...r,
      product,
      requestId,
      callbackUrl,
      apiBody,
      subject,
      emailText
    }
  };
});

const batchContext = $node["Batch Process"].context;
if (batchContext?.continue === true) {
  return items;
}
return items;

// Score and validate offers
const now = new Date().toISOString();
const required = ['reseller_id', 'price', 'currency'];
const errors = [];

required.forEach(field => {
  if (!$json[field]) errors.push(`Missing: ${field}`);
});

const price = Number($json.price);
if (isNaN(price) || price <= 0) errors.push('Invalid price');
if (price > 1000000) errors.push('Price too high');

const validCurrencies = ['USD', 'EUR', 'GBP', 'CHF', 'JPY'];
if (!validCurrencies.includes($json.currency)) {
  errors.push('Invalid currency');
}

if (errors.length) throw new Error(errors.join(', '));

// Enhanced scoring
const responseTime = Number($json.response_time || 300);
const trustScore = Number($json.trust_score || 5.0);

let score = price;

// Time penalty (exponential after 5 min)
const timePenalty = responseTime > 300 ? 
  Math.pow((responseTime - 300) / 60, 1.5) * 50 : 0;
score -= timePenalty;

// Trust bonus
const trustBonus = (trustScore - 5) * 100;
score += trustBonus;

// Currency adjustment
const currencyMultipliers = {
  'USD': 1.0, 'EUR': 0.85, 'GBP': 0.80, 'CHF': 0.90, 'JPY': 0.70
};
score *= (currencyMultipliers[$json.currency] || 0.75);

// Terms bonus
const terms = ($json.terms || '').toLowerCase();
let termsBonus = 0;
if (terms.includes('wire') || terms.includes('bank')) termsBonus += 50;
if (terms.includes('24') || terms.includes('immediate')) termsBonus += 30;
if (terms.includes('certified')) termsBonus += 20;
score += termsBonus;

return [{
  json: {
    timestamp: now,
    reseller_id: $json.reseller_id,
    reseller_name: $json.reseller_name || '',
    price: price,
    currency: $json.currency,
    terms: $json.terms || '',
    contact: $json.contact || '',
    response_time_s: responseTime,
    trust_score: trustScore,
    region: $json.region || 'Unknown',
    notes: $json.notes || '',
    score: Math.round(score * 100) / 100
  }
}];

// Generate summary and rankings
const limit = Number($parameter.limit) || 5;
const timeFilter = $parameter.timeFilter || '24h';

let offers = items.map(i => i.json).filter(r => r.price && !isNaN(r.price));

// Apply time filter
if (timeFilter !== 'all') {
  const timeFilters = {
    '1h': 1 * 60 * 60 * 1000,
    '24h': 24 * 60 * 60 * 1000,
    '7d': 7 * 24 * 60 * 60 * 1000
  };

  if (timeFilters[timeFilter]) {
    const cutoff = new Date(Date.now() - timeFilters[timeFilter]);
    offers = offers.filter(o => new Date(o.timestamp) > cutoff);
  }
}

// Sort by score
offers.sort((a, b) => Number(b.score) - Number(a.score));

// Calculate analytics
const analytics = {
  total_offers: offers.length,
  unique_resellers: new Set(offers.map(o => o.reseller_id)).size,
  avg_price: offers.length > 0 ? 
    Math.round(offers.reduce((sum, o) => sum + Number(o.price), 0) / offers.length) : 0,
  avg_response_time: offers.length > 0 ? 
    Math.round(offers.reduce((sum, o) => sum + Number(o.response_time_s || 0), 0) / offers.length) : 0,
  price_range: {
    min: offers.length > 0 ? Math.min(...offers.map(o => Number(o.price))) : 0,
    max: offers.length > 0 ? Math.max(...offers.map(o => Number(o.price))) : 0
  }
};

// Top offers
const topOffers = offers.slice(0, limit).map((offer, index) => ({
  rank: index + 1,
  ...offer,
  price_formatted: `${offer.currency} ${Number(offer.price).toLocaleString()}`
}));

return [{
  json: {
    generated_at: new Date().toISOString(),
    time_filter: timeFilter,
    analytics,
    top_offers: topOffers
  }
}];

